INSERT INTO `conceptos` (`id`, `titulo`, `contenido`, `fecha`, `noreloj`, `imagen`, `subtitulo`) VALUES
(4, 'MQE', 'Es un rol en la industria que se enfoca en asegurar que los productos se fabriquen cumpliendo con los estándares de calidad establecidos...', '2025-05-20 15:37:07', 'MW25020029', '682ca1a3ec560_MQE.jpg', 'Manufacturing Quality Engineer'),
(5, 'AC Cycle', 'Consiste en encender y apagar repetidamente un dispositivo bajo corriente alterna...', '2025-05-20 15:37:47', 'MW25020029', '682ca1cb2c880_I1.jpg', 'Ciclo de Corriente Alterna'),
(6, 'DC Cycle', 'Proceso de aplicar y quitar energía DC repetidamente a un sistema...', '2025-05-20 15:38:26', 'MW25020029', '682ca1f2443d8_I2.jpg', 'Ciclo de Corriente Continua'),
(7, 'OS Reboot', 'Proceso mediante el cual el sistema operativo se reinicia completamente sin cortar la energía física del equipo. Se utiliza para validar la estabilidad del software y hardware tras un reinicio.', '2025-05-20 15:39:09', 'MW25020029', '682ca21d510f3_I3.jpg', 'Reinicio del Sistema Operativo'),
(8, 'BMC', 'Es un microcontrolador integrado en servidores que permite la gestión remota del sistema, incluyendo encendido/apagado, monitoreo de sensores, y acceso remoto a la consola, incluso si el sistema operativo no responde.', '2025-05-20 15:39:47', 'MW25020029', '682ca243164ff_I4.jpg', 'Baseboard Management Controller'),
(9, 'Servidor', 'Es un equipo diseñado para proporcionar servicios, recursos o datos a otras computadoras en una red. Generalmente se caracteriza por su alto rendimiento, disponibilidad y capacidad de operación continua.', '2025-05-20 15:40:17', 'MW25020029', '682ca26168e21_I5.jpg', 'Servidor'),
(10, 'Blade', 'Es un servidor modular que se inserta en un chasis común con otros blades. Comparte energía, refrigeración y red, optimizando espacio y eficiencia en centros de datos.', '2025-05-20 15:40:45', 'MW25020029', '682ca27dbe52d_I6.jpg', 'Blade Server'),
(11, 'CPLD', 'Es un dispositivo lógico programable que permite implementar funciones de control de hardware como gestión de señales, encendido, reinicios, entre otros, siendo clave en placas base y servidores.', '2025-05-20 15:41:18', 'MW25020029', '682ca29e9a25e_I7.jpg', 'Complex Programmable Logic Device'),
(12, 'BIOS', 'Firmware que se ejecuta al encender el equipo y se encarga de inicializar el hardware y cargar el sistema operativo. Permite la configuración básica del sistema a bajo nivel.', '2025-05-20 15:43:24', 'MW25020029', '682ca31c3827b_I8.jpg', 'Basic Input/Output System'),
(13, 'HPM', 'Conjunto de especificaciones que define cómo se gestiona el hardware en plataformas como servidores, incluyendo actualizaciones de firmware, monitoreo de sensores y gestión remota.', '2025-05-20 15:44:56', 'MW25020029', '682ca378d4c62_I9.jpg', 'Hardware Platform Management'),
(14, 'Cerberus', 'Es un sistema interno utilizado para monitorear y registrar eventos críticos de hardware en pruebas. Permite validar la integridad de los ciclos de encendido, fallos y respuestas del sistema durante validaciones de calidad.', '2025-05-20 15:45:23', 'MW25020029', '682ca39344514_I10.jpg', 'Cerberus'),
(15, 'IPv6', 'Protocolo de red que reemplaza a IPv4, diseñado para resolver la escasez de direcciones. Soporta 128 bits por dirección y mejora seguridad y rendimiento en la comunicación de red.', '2025-05-20 15:46:30', 'MW25020029', '682ca3d695423_I11.jpg', 'IPv6 (Internet Protocol v6)'),
(16, 'IPv4', 'Protocolo de red más utilizado históricamente. Usa direcciones de 32 bits y permite la comunicación entre dispositivos en redes LAN y WAN.', '2025-05-20 15:47:00', 'MW25020029', '682ca3f42d608_I12.jpg', 'IPv4 (Internet Protocol v4)'),
(17, 'Máquina Virtual', 'Es una emulación de un sistema operativo o entorno de hardware que corre dentro de otro sistema. Permite ejecutar múltiples sistemas operativos sobre el mismo hardware físico.', '2025-05-20 15:47:31', 'MW25020029', '682ca4130fefc_I13.jpg', 'Máquina Virtual'),
(18, 'Manticore', 'Es una plataforma interna utilizada para pruebas automatizadas de hardware. Permite ejecutar test de validación y control de firmware en servidores y sistemas complejos.', '2025-05-20 15:47:57', 'MW25020029', '682ca42d2c88e_I14.jpg', 'Manticore'),
(19, 'TIP', 'Framework o infraestructura que permite ejecutar pruebas en múltiples plataformas de hardware y firmware, recopilando datos y resultados de forma centralizada.', '2025-05-20 15:48:26', 'MW25020029', '682ca44a5a18a_I15.jpg', 'Test Infrastructure Platform'),
(20, 'Glacier Peak', 'Controlador especializado usado en servidores para gestionar puertos de expansión y comunicación de baja velocidad, especialmente en plataformas de validación.', '2025-05-20 15:48:49', 'MW25020029', '682ca461055d9_I16.jpg', 'Glacier PIC'),
(21, 'E1.S', 'Especificación de factor de forma para SSDs NVMe, optimizada para centros de datos. Ofrece mayor densidad, eficiencia térmica y rendimiento que M.2 en servidores.', '2025-05-20 15:49:19', 'MW25020029', '682ca47f95d09_I17.jpg', 'E1.S (Enterprise and Datacenter SSD)'),
(22, 'M.2', 'Formato físico compacto para unidades SSD y tarjetas Wi-Fi. Se conecta directamente a la placa base y ofrece alta velocidad mediante interfaces como NVMe o SATA.', '2025-05-20 15:49:44', 'MW25020029', '682ca49869154_I18.jpg', 'M.2'),
(23, 'CloudMoS', 'Es una plataforma interna basada en la nube para monitoreo, validación y gestión de pruebas en sistemas de hardware, utilizada durante el desarrollo de servidores.', '2025-05-20 15:50:14', 'MW25020029', '682ca4b6c60b7_I19.jpg', 'Cloud MOST');

